import React, { Component } from 'react'
import Nitem from './Nitem'

export class News extends Component {
  articles = [
    {

    }
  ]
  constructor(){
    super();
    console.log("hello i am a connstructor from news component");
    this.state = {
      articles:this.articles,
      loading:false
    }
  }

  render() {
    return (
      <div className="container my-3">
          <h2>News-Fire..!-Top Headlines.</h2>
          <div className="row">
          {this.state.articles.map((element)=>{
            return <div className="col-md-4" key={element.url}>
                      <Nitem  title={element.title} description={element.description} imageUrl={element.urlToImage} newsUrl={element}/>
                   </div>
          })}
          </div>
      </div>
    )
  }
}

export default News
