import React, { Component } from 'react'
import Nitem from './Nitem'

export class News extends Component {

  constructor(){
    super();
    console.log("hello i am a connstructor from news component");
    this.state = {
      articles:[],
      loading:false,
      page:1
    }
  }

  async componentDidMount(){
    let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=5bfbfac022d543f88f61ec7650f30c7b&";
    let data = await fetch(url);
    let parsedData = await data.json()
    console.log(parsedData);
    this.setState({articles:parsedData.articles})
  }

  handlePrevClick = async () =>{
    console.log("previous")
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=5bfbfac022d543f88f61ec7650f30c7b&page=${this.state.page - 1}&pageSize=20`;
        let data = await fetch(url);
        let parsedData = await data.json()
        console.log(parsedData);
        this.setState({
            page:this.state.page - 1,
            articles:parsedData.articles
        })
  }

  handleNextClick = async () =>{
        console.log("next")
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=5bfbfac022d543f88f61ec7650f30c7b&page=${this.state.page + 1}&pageSize=20`;
        let data = await fetch(url);
        let parsedData = await data.json()
        console.log(parsedData);
        this.setState({
            page:this.state.page + 1,
            articles:parsedData.articles
        })
  }

  render() {
    return (
      <div className="container my-3">
          <h1>News-Fire..!-Top Headlines.</h1>
          <div className="row">
          {this.state.articles.map((element)=>{
            return <div className="col-md-4" key={element.url}>
                      <Nitem  title={element.title?element.title:""} description={element.description?element.description:""} imageUrl={element.urlToImage} newsUrl={element.url}/>
                   </div>
          })}
          </div>
          <div className="container d-flex justify-content-between">
            <button disabled={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.handlePrevClick}> &larr; Previous</button>
            <button type="button" className="btn btn-dark" onClick={this.handleNextClick}>Dark &larr; </button>
          </div>
      </div>
    )
  }
}

export default News
