  render() {
    return (
      <div className="container my-3">
          <h2>News-Fire..!-Top Headlines.</h2>
          <div className="row">
          {this.state.articles.map((element)=>{
            return <div className="col-md-4" key={element.url}>
                      <Nitem  title={element.title.slice(0,45)} description={element.description.slice(0,85)} imageUrl={element.urlToImage} newsUrl={element.url}/>
                   </div>
          })}
          </div>
      </div>
    )
  }
}

export default News
